//Лабораторная работа №4 30.09. Задание 1 Дана последовательность. Определить максимальный размер подпоследовательности, состоящей из одинаковых элементов. //using System;class HelloWorld {	static void Main() {		int An, A1, A2, raz=1, razmax=0;		A1=Convert.ToInt32(Console.ReadLine());		An=Convert.ToInt32(Console.ReadLine());		for (int i=1; i < An; i++) {			A2=Convert.ToInt32(Console.ReadLine());			if (A2==A1) {				raz=raz+1;				if(raz>razmax) razmax=raz;				A1=A2;			}			else {				if(raz>razmax) razmax=raz;				raz=1;				A1=A2;			}		}		Console.WriteLine(razmax);	}}//Намятова Анастасия Валерьевна ФИТ-242//